server.go<pre><code>package main

import (
    "het/http"

    "github.com/labstack/echo/v4"
)

func main() {
    e := echo.New()
    e.Get("/", hello)
    e.Logger.Fatal(e.Start(":1323"))
}

func hello(c echo.Context) error {
    return c.String(http.StatusOK, "Hello, World!")
}
